package success.planfit.utils;

import jakarta.persistence.EntityManager;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Component;
import success.planfit.domain.course.SpaceType;
import success.planfit.domain.embeddable.SpaceInformation;
import success.planfit.domain.user.IdentityType;
import success.planfit.dto.request.PlanfitUserSignUpRequestDto;
import success.planfit.dto.response.TokenResponseDto;
import success.planfit.jwt.TokenProvider;
import success.planfit.jwt.TokenType;
import success.planfit.photo.PhotoProvider;
import success.planfit.photo.PhotoType;
import success.planfit.service.AuthorizationService;

import java.time.LocalDate;

@RequiredArgsConstructor
@Slf4j
@Component
public class TestUtil {

    private static final String BASIC_NAME = "helloName";
    private static final String BASIC_LOGIN_ID = "helloId";
    private static final String BASIC_PASSWORD = "helloPassword1234!";
    private static final String BASIC_PHONE_NUMBER = "010-1234-5678";
    private static final LocalDate BASIC_BIRTH_OF_DATE = LocalDate.now();
    private static final IdentityType BASIC_IDENTITY = IdentityType.STUDENT;
    private static final String BASIC_EMAIL = "hello@email.com";
    private static final String BASIC_SPACE_NAME = "helloSpaceName";
    private static final String BASIC_LOCATION = "helloLocation";
    private static final SpaceType BASIC_SPACE_TYPE = SpaceType.TYPE;
    private static final String BASIC_LINK = "http://hello.link.com";
    private static final double BASIC_LATITUDE = 1.234567;
    private static final double BASIC_LONGITUDE = 1234.5678;

    private final String BASIC_PROFILE_PHOTO_STRING = getEncodedImage();

    private final TokenProvider tokenProvider;
    private final AuthorizationService authorizationService;

    public void clearEntityManager(EntityManager em) {
        em.flush();
        em.flush();

        log.info("EntityManager has been cleared");
    }

    public String getEncodedImage() {
        return "";
    }

    public byte[] getBinaryImage() {
        return PhotoProvider.decode(getEncodedImage());
    }

    public long getUserIdFromJwt(String token) {
        return Long.parseLong(tokenProvider.parseClaims(token)
                .getSubject());
    }

    public boolean validateToken(String token, TokenType tokenType) {
        return tokenProvider.validateToken(token, tokenType);
    }

    /**
     * 플랜핏 회원을 AuthorizationService를 통해 회원가입시키고, 그 회원에 대한 정보를 반환하는 메서드. <br>
     * @return UserInfo
     */
    public UserInfo signUpPlanfitUser() {
        TokenResponseDto responseDto = authorizationService.planfitSignUp(PlanfitUserSignUpRequestDto.builder()
                .name(BASIC_NAME)
                .loginId(BASIC_LOGIN_ID)
                .password(BASIC_PASSWORD)
                .phoneNumber(BASIC_PHONE_NUMBER)
                .birthOfDate(BASIC_BIRTH_OF_DATE)
                .identity(BASIC_IDENTITY)
                .email(BASIC_EMAIL)
                .profilePhoto(BASIC_PROFILE_PHOTO_STRING)
                .photoType(PhotoType.ENCODED_BINARY)
                .build()
        );

        return UserInfo.builder()
                .userId(getUserIdFromJwt(responseDto.accessToken()))
                .accessToken(responseDto.accessToken())
                .refreshToken(responseDto.refreshToken())
                .build();
    }

    public SpaceInformation getBasicSpaceInformation() {
        return SpaceInformation.builder()
                .spaceName(BASIC_SPACE_NAME)
                .location(BASIC_LOCATION)
                .spaceTag(BASIC_SPACE_TYPE)
                .link(BASIC_LINK)
                .latitude(BASIC_LATITUDE)
                .longitude(BASIC_LONGITUDE)
                .build();
    }
}
